$(function(){$.ajax({type:"POST",url:"/bin/discover/navigation.json",data:{publication:$(".discoverpublication .leftarrow").data("path")},success:function(b){var a=jQuery.parseJSON(b);
$(".discoverpublication .leftarrow > a").attr("href",a.previous);
$(".discoverpublication .rightarrow > a").attr("href",a.next)
},error:function(a,c,b){$(".discoverpublication .leftarrow").hide();
$(".discoverpublication .rightarrow").hide()
}});
$(".discover-image-caption-icon").click(function(){$(this).hide();
$(this).next().show()
});
$(".discover-image-caption .close-icon").click(function(){$(this).parent().hide();
$(this).parent().prev().show()
})
});